// ==UserScript==
// @name           BookmarkOpt.uc.js
// @description    书签操作增强
// @author         Ryan
// @include        main
// @include        chrome://browser/content/places/places.xhtml
// @include        chrome://browser/content/places/bookmarksSidebar.xhtml
// @include        chrome://browser/content/places/historySidebar.xhtml
// @version        1.0
// @shutdown       window.BookmarkOpt.destroy();
// @homepage       https://github.com/benzBrake/FirefoxCustomize/tree/master/userChromeJS
// @note           合并 AddBookmarkHere 和 UpdateBookmarkLite，支持复制书签链接和标题
// ==/UserScript==
(function (css) {

    if (window.BookmarkOpt) {
        window.BookmarkOpt.destroy();
        delete window.BookmarkOpt;
    }

    let { classes: Cc, interfaces: Ci, utils: Cu, results: Cr } = Components;

    const LANG = {
        'zh-CN': {
            "add bookmark here": "添加书签到此处",
            "add bookmark here tooltip": "左键：添加到最后\nShift+左键：添加到最前",
            "update current bookmark": "替换为当前网址",
            "update current bookmark tooltip": "左键：替换当前网址\n中键：替换当前地址和标题\n右键：替换当前网址和自定义当前标题",
            "update current bookmark prompt": "更新当前书签标题，原标题为：\n %s",
            "copy bookmark title": "复制标题",
            "copy bookmark link": "复制链接",
        },
        'en-US': {
            "add bookmark here": "Add Bookmark Here",
            "copy bookmark title": "Copy Title",
            "copy bookmark link": "Copy URL",
        }
    }

    const _LOCALE = LANG.hasOwnProperty(Services.locale.appLocaleAsBCP47) ? Services.locale.appLocaleAsBCP47 : 'zh-CN';

    // 右键菜单
    const PLACES_CONTEXT_ITEMS = [{
        id: 'placesContext_add:bookmark',
        label: $L("add bookmark here"),
        tooltiptext: $L("add bookmark here tooltip"),
        accesskey: "h",
        insertBefore: "placesContext_show_bookmark:info",
        condition: "toolbar bookmark folder",
        image: "data:image/svg+xml;base64,",
        oncommand: "window.BookmarkOpt.handleEvent(event, 'add', this.parentNode.triggerNode)"
    }, {
        id: "placesContext_update_bookmark:info",
        label: $L("update current bookmark"),
        tooltiptext: $L("update current bookmark tooltip"),
        accesskey: "u",
        insertBefore: "placesContext_show_bookmark:info",
        condition: "bookmark",
        'image': "data:image/svg+xml;base64,",
        oncommand: "window.BookmarkOpt.handleEvent(event, 'update', this.parentNode.triggerNode)",
    }, {
        label: $L("copy bookmark title"),
        insertBefore: "placesContext_paste_group",
        condition: "bookmark history",
        accesskey: "A",
        oncommand: "window.BookmarkOpt.handleEvent(event, 'copyTitle', this.parentNode.triggerNode)",
        image: "data:image/svg+xml;base64,"
    }, {
        label: $L("copy bookmark link"),
        insertBefore: "placesContext_paste_group",
        condition: "bookmark history",
        accesskey: "L",
        text: "[%TITLE%](%URL%)",
        oncommand: "window.BookmarkOpt.handleEvent(event, 'copyLink', this.parentNode.triggerNode)",
        image: "data:image/svg+xml;base64,",
        accesskey: "L"
    }];

    // 书签弹出面板菜单
    const PLACES_POPUP_ITEMS = [{
        'label': $L("add bookmark here"),
        'tooltiptext': $L("add bookmark here tooltip"),
        'image': "data:image/svg+xml;base64,",
        oncommand: "window.BookmarkOpt.handleEvent(event, 'panelAdd', this.parentNode)"
    }, {

    }];

    window.BookmarkOpt = {
        items: [],
        addPlaceContextItem: function (refNode) {
            PLACES_CONTEXT_ITEMS.forEach(p => {
                let item = $C('menuitem', p, document, refNode);
                if (!p.condition) item.setAttribute('condition', 'all');
                this.items.push(item);
                ($(p.insertBefore) || refNode || $('#placesContext').firstChild).insertAdjacentElement('beforebegin', item);
            });

            $('placesContext').addEventListener('popupshowing', this.handleMenuItem, false);
            $('placesContext').addEventListener('popuphidden', this.handleMenuItem, false);
        },

        addPanelItem: function (event) {
            $('PlacesToolbarItems').addEventListener('popupshowing', this.handlePanelItem, false);
            $('PlacesToolbarItems').addEventListener('popuphidden', this.handlePanelItem, false);
        },
        handleMenuItem: function (event) {
            if (event.type === 'popuphidden') {
                event.target.querySelectorAll('.bmopt').forEach(el => el.hidden = false);
            } else {
                event.target.querySelectorAll('.bmopt').forEach(el => el.hidden = true);
                let state = ['all'],
                    triggerNode = event.currentTarget.triggerNode,
                    view = PlacesUIUtils.getViewForNode(triggerNode);

                if (triggerNode.id === "PlacesToolbarItems") { state.push('toolbar'); }
                else if (view?.selectedNode.type == 0) {
                    if (BookmarkOpt.isMain) state.push('bookmark')
                    else if (BookmarkOpt.isBookarmkSidebar) state.push('bookmark')
                    else if (BookmarkOpt.isHistorySidebar) state.push('history')
                }
                else if (view.selectedNode.type == 6) state.push('folder');

                event.currentTarget.setAttribute("bmopt", state.join(" "));
                state.length > 0 && event.target.querySelectorAll(
                    state.map(s => `.bmopt[condition~="${s}"]`).join(', ')
                ).forEach(m => {
                    m.removeAttribute('disabled');
                    m.hidden = false;
                });
            }


        },
        handlePanelItem: function (event) {
            if (event.type === 'popuphidden') {
                // 防止影响其他方式添加书签
                BookmarkOpt.clearPanelItems(event.target);
            } else if (event.type === 'popupshowing') {
                let firstItem = event.target.firstChild;
                if (firstItem?.classList.contains('bmopt-panel')) return;
                let last;
                PLACES_POPUP_ITEMS.forEach(c => {
                    let item;
                    if (c.label) {
                        item = $C('menuitem', c);
                        item.classList.add('bmopt-panel');
                    } else {
                        item = $C('menuseparator', {
                            'class': 'bmopt-separator'
                        })
                    }
                    if (last) {
                        last.insertAdjacentElement('afterend', item);
                    } else {
                        firstItem.parentNode.insertBefore(item, firstItem);
                    }
                    last = item;
                });
            }
        },
        clearPanelItems: function (aDoc) {
            let menuitems = (aDoc || document).querySelectorAll(".bmopt-panel"),
                menuseparators = (aDoc || document).querySelectorAll(".bmopt-separator");
            for (let menuitem of menuitems) {
                menuitem.parentNode.removeChild(menuitem);
            }
            for (let menuseparator of menuseparators) {
                menuseparator.parentNode.removeChild(menuseparator);
            }
        },
        handleUrlbar: function (e) {
            if (e.button == 0) {
                var bar = e.target.ownerGlobal.document.getElementById("PersonalToolbar");
                setToolbarVisibility(bar, bar.collapsed);
            }
        },
        get window() {
            const wm = Cc["@mozilla.org/appshell/window-mediator;1"].getService(Ci.nsIWindowMediator);
            return wm.getMostRecentWindow("navigator:browser");
        },
        get isBookarmkSidebar() {
            return location.href === "chrome://browser/content/places/bookmarksSidebar.xhtml";
        },
        get isHistorySidebar() {
            return location.href === "chrome://browser/content/places/historySidebar.xhtml"
        },
        get isMain() {
            return location.href === "chrome://browser/content/browser.xhtml"
        },
        handleEvent: function (event, aMethod, aTriggerNode) {
            let popupNode = aTriggerNode || PlacesUIUtils.lastContextMenuTriggerNode || document.popupNode;
            if (!popupNode) return;
            let view = PlacesUIUtils.getViewForNode(popupNode),
                selectedNode = view.selectedNode || popupNode._placesNode,
                pageTitle = BookmarkOpt.window.gBrowser.contentTitle,
                isFolder = selectedNode?.type !== 0

            switch (aMethod) {
                case 'panelAdd':
                    BookmarkOpt.clearPanelItems(aTriggerNode); // 新增的项目会影响书签插入，必须清掉
                case 'add':
                    let iid, aid;
                    if (selectedNode) {
                        if (isFolder) {
                            iid = selectedNode.itemId;
                            aid = event.shiftKey ? 0 : BookmarkOpt.bmService.DEFAULT_INDEX;
                        } else {
                            iid = BookmarkOpt.bmService.getFolderIdForItem(selectedNode.itemId);
                            var id = selectedNode.itemId;
                            aid = event.shiftKey ? id : id + 1;
                        }
                    } else {
                        iid = view.result.root.folderItemId;
                        aid = event.shiftKey ? 0 : BookmarkOpt.bmService.DEFAULT_INDEX;
                    }
                    var uri = Services.io.newURI(BookmarkOpt.window.gBrowser.currentURI.spec, null, null);
                    this.bmService.insertBookmark(iid, uri, aid, pageTitle);
                    break;
                case 'update':
                    let guid = selectedNode.bookmarkGuid;
                    if (!guid) return;
                    let info = {
                        guid,
                        title: selectedNode.title,
                        url: BookmarkOpt.window.gBrowser.currentURI.spec,
                    }
                    if (event.button === 1) {
                        info.title = pageTitle;
                    } else if (event.button === 2) {
                        const title = window.prompt($L("update current bookmark prompt", selectedNode.title), pageTitle);
                        if (title === null) return;
                        if (title !== selectedNode.title)
                            info.title = title;
                    }
                    PlacesUtils.bookmarks.update(info);
                    break;
                case 'nodeType':
                    alert(selectedNode.type);
                    break;
                case 'copyTitle':
                    copyText(selectedNode.title);
                    break
                case 'copyLink':
                    let text = event.currentTarget.getAttribute("text") || "%URL%";
                    text = text.replace("%TITLE%", (selectedNode.title === "" ? selectedNode.uri : (selectedNode.title || "")).replace('[', '【').replace("]", "】")).replace("%URL%", selectedNode.uri)
                    copyText(text);
                    break;
                default:
                    break;
            }
        },
        init: function () {
            this.bmService = Cc["@mozilla.org/browser/nav-bookmarks-service;1"].getService(Ci.nsINavBookmarksService);
            this.addPlaceContextItem();
            if (location.href == "chrome://browser/content/browser.xhtml") {
                this.addPanelItem();
                this.urlbar = document.getElementById('urlbar');
                this.urlbar?.addEventListener('dblclick', BookmarkOpt.handleUrlbar, false);

            }
            this.style = addStyle(css);

        },
        destroy: function () {
            this.clearPanelItems();
            this.items.array.forEach(element => {
                element.remove();
            });
            this.urlbar?.removeEventListener('dblclick', BookmarkOpt.handleUrlbar, false);
            if (this.style && this.style.parentNode) this.style.parentNode.removeChild(this.style);
            delete window.BookmarkOpt;
        },
    }
    window.BookmarkOpt.init();

    function $(id, aDoc) {
        id = id || "";
        let doc = aDoc || document;
        if (id.startsWith('#')) id = id.substring(1, id.length);
        return doc.getElementById(id);
    }

    function $C(type, props = {}, aDoc) {
        let doc = aDoc || document;
        let el = doc.createXULElement(type);
        for (let p in props) {
            if (type === 'menuitem' && p === 'image') el.classList.add('menuitem-iconic');
            el.setAttribute(p, props[p]);
        }
        el.classList.add('bmopt');
        return el;
    }

    function addStyle(css) {
        var pi = document.createProcessingInstruction(
            'xml-stylesheet',
            'type="text/css" href="data:text/css;utf-8,' + encodeURIComponent(css) + '"'
        );
        return document.insertBefore(pi, document.documentElement);
    }

    function copyText(aText) {
        Cc["@mozilla.org/widget/clipboardhelper;1"].getService(Ci.nsIClipboardHelper).copyString(aText);
    }

    function $L(key, replace) {
        let str = LANG[_LOCALE].hasOwnProperty(key) ? LANG[_LOCALE][key] : (LANG['zh-CN'].hasOwnProperty(key) ? LANG['zh-CN'][key] : "");
        str = str.replace("%s", replace || "");
        return str;
    }

})(`
.bmopt-separator+menuseparator{
    display: none;
}
`)